mapper.select:
select *, date_format(regdate, '%Y-%m-%d %T') fmtdate
from post
where categoryid = #{categoryid};

select count(*)
from post
where categoryid = 107 and userid = #{userid}

select p.*, u.nickname username, c.commentid, c.content admin_content, c.regdate admin_regdate from post p left join comment c ON p.postid = c.postid join user u ON p.userid = u.userid where p.categoryid = #{categoryId}
and c.postid is null
and c.postid is not null
